}

#include <stdio.h>
int main() {
	char c = 'a';
	int i = 97;
	printf("%c,%d\n", c, c);
	printf("%c,%d\n", i, i);
	return 0;
}

import java.util.*;
public class Main {
	public static void main(String args[]) {
		char c='a';
		int i=97;
		System.out.printf("%c,%d\n",c,(int)c);
		System.out.printf("%c,%d\n",i,i);
	}
}
