#include <stdio.h>
int main() {
	float f = 123.456;
	printf("%f,%10f,%10.2f,%.2f,%-10.2f\n", f, f, f, f, f);
	return 0;
}

import java.util.*;
public class Main {
	public static void main(String args[]) {
		float f=(float)123.456;
		System.out.printf("%f,%10f,%10.2f,%.2f,%-10.2f\n",f,f,f,f,f);
	}
}
