import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		final int MAX=1024;
		int i = 0;
		@SuppressWarnings("resource")
		Scanner in = new Scanner(System.in);
		int[] a =new int[MAX];
		int[] b =new int[MAX];
		a[i] = in.nextInt();
		b[i] = in.nextInt();
		while(a[i] != 0 && b[i] != 0){
			i++;
			a[i] = in.nextInt();
			b[i] = in.nextInt();
		}

		for(int j = 0; j < i; j++){
			System.out.println(a[j]+b[j]);
		}
	}
}

