#include<stdio.h>
int main()
{
	int a,b,sum;
	while(scanf("%d%d",&a,&b)!=EOF)
	{
		sum=a+b;
		printf("%d\n",sum);
		printf("\n");
	}
	return 0;
}

#include<stdio.h>

int main()
{
  int a,b;
  while(scanf("%d%d",&a,&b)!=EOF)
    printf("%d\n\n",a+b);
  return 0;
}

