import java.util.*;

public class Main {

	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);

		while (scanner.hasNext()){
			int n1 = scanner.nextInt();
			int n2 = scanner.nextInt();


			System.out.println(n1 + n2);
			System.out.println();
		}


	}

}

